import { ethers } from "ethers";

// Contract ABI (Replace with your contract's ABI)
/*const contractABI = [
  {
    "inputs": [
        {
          "indexed": true,
          "internalType": "address",
          "name": "doctorAddress",
          "type": "address"
        },
        {
          "indexed": false,
          "internalType": "string",
          "name": "username",
          "type": "string"
        }
      ],
    "name": "registerDoctor",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  }
];*/

import healthcareSystemAbi from "../contracts/HealthcareSystem.json";

const contractABI = healthcareSystemAbi.abi;

// Replace with your deployed contract address
const contractAddress = "0x8c651Af7c6D183ad95A2338F2155B68096a1c6b6";

export const getEthereumContract = async () => {
  if (window.ethereum) {
    const provider = new ethers.BrowserProvider(window.ethereum);
    const signer = await provider.getSigner();
    const contract = new ethers.Contract(contractAddress, contractABI, signer);
    return contract;
  } else {
    console.error("Ethereum wallet not found");
    return null;
  }
};
